(3, 1, 0, 'footerBase', '', 0, 2, 0, '</div>\r\n<footer class="[[!selectFooterStyle]]">\r\n    <div class="inblock">\r\n            <address>\r\n                    <p class="title">[[%address_name_string? &topic=`default` &namespace=`babel`]]</p>\r\n                    <p>[[%address_address_string? &topic=`default` &namespace=`babel`]]</p>\r\n                    <p>[[%address_phone_string? &topic=`default` &namespace=`babel`]]</p>\r\n                    <p>[[%address_email1_string? &topic=`default` &namespace=`babel`]]</p>\r\n                    <p>[[%address_email2_string? &topic=`default` &namespace=`babel`]]</p>\r\n            </address>\r\n            <ul class="social">\r\n                    <li class="fb"><a href="#">facebook</a></li>\r\n                    <li class="tw"><a href="#">vkontakte</a></li>\r\n            </ul>\r\n            <!--div class="cpr">\r\n                    <p>[[%lf_footer_copyright]]</p>\r\n                    <a href="#">Legal notice</a>\r\n            </div-->	\r\n            <!--p class="mbc">Сайты-визитки от AMT<a href="#">AMT</a></p-->\r\n    </div>\r\n</footer>\r\n</body>\r\n</html>', 0, NULL, 1, 'assets/basetheme-elements/chunks/base/footerBase.tpl'),
