(45, 1, 0, 'indexHeader', '', 0, 18, 0, '<div class="wrapper">\r\n    <div class="sectionName s1">\r\n        <i></i>\r\n        <span>[[*pagetitle]]</span>\r\n    </div>\r\n<header class="header" id="header">\r\n                \r\n    <div class="inblock">\r\n        <a href="#" class="logo">azovmashtest.com.ua</a>\r\n        <h1>[[%header_title? &topic=`default` &namespace=`babel`]]</h1>\r\n        <p class="help"><sup>*</sup>[[%header_sup_title? &topic=`default` &namespace=`babel`]]</p>\r\n        <ul class="social">\r\n                <li class="fb"><a href="#">facebook</a></li>\r\n                <li class="tw"><a href="#">vkontakte</a></li>\r\n                <!--li class="yt"><a href="#">youtube</a></li-->\r\n        </ul>\r\n        [[$topMenuCommon]]\r\n        <span class="more" id="more-btn">[[%main_more_button? &topic=`default` &namespace=`babel`]]</span>\r\n    </div>\r\n\r\n</header>\r\n        ', 0, 'a:0:{}', 1, 'assets/basetheme-elements/chunks/partsHeader/indexHeader.tpl'),
