(1, 1, 0, 'formContacts', '', 0, 1, 0, 'function validateEmail($email){\r\n    $pattern = "/^[^@]{1,64}\\@[^\\@]{1,255}$/";\r\n    $condition = @preg_match($pattern, $email);\r\n    if (!$condition) {\r\n        return true;\r\n    }\r\n\r\n    $email_array = explode("@", $email);\r\n    $local_array = explode(".", $email_array[0]);\r\n    for ($i = 0; $i < sizeof($local_array); $i++) {\r\n        $pattern = "/^(([A-Za-z0-9!#$%&''*+\\/=?^_`{|}~-][A-Za-z0-9!#$%&''*+\\/=?^_`{|}~\\.-]{0,63})|(\\"[^(\\\\|\\")]{0,62}\\"))$/";\r\n        $condition = @preg_match($pattern,$local_array[$i]);\r\n        if (!$condition) {\r\n            return true;\r\n        }\r\n    }\r\n\r\n    $pattern = "^\\[?[0-9\\.]+\\]?$";\r\n    $condition = @preg_match($pattern, $email_array[1]);\r\n    if (!$condition) {\r\n        $domain_array = explode(".", $email_array[1]);\r\n        if (sizeof($domain_array) < 2) {\r\n            return true;\r\n        }\r\n        for ($i = 0; $i < sizeof($domain_array); $i++) {\r\n            $pattern = "/^(([A-Za-z0-9][A-Za-z0-9-]{0,61}[A-Za-z0-9])|([A-Za-z0-9]+))$/";\r\n            $condition = @preg_match($pattern,$domain_array[$i]);\r\n            if (!$condition) {\r\n                return true;\r\n            }\r\n        }\r\n    }\r\n    return false;\r\n}\r\n\r\n$properties =& $scriptProperties;\r\n\r\n$properties[''fields''] = !empty($properties[''fields'']) ? explode('','',$properties[''fields'']) : array(''name'',''email'',''message'');\r\n$properties[''validate''] = !empty($properties[''validate'']) ? explode('','',$properties[''validate'']) : array(''name'',''email'',''message'');\r\n$properties[''emailTo''] = !empty($properties[''emailTo'']) ? $properties[''emailTo''] : $modx->getOption(''emailsender'');;\r\n$properties[''subject''] = !empty($properties[''subject'']) ? $properties[''subject''] : ''Contact Form'';\r\n$properties[''tpl''] = !empty($properties[''tpl'']) ? $properties[''tpl''] : ''emailContacts'';\r\n\r\n$fields = array();\r\nforeach($properties[''fields''] as $fieldName) {\r\n    $fields[$fieldName] = isset($_POST[$fieldName]) ? $_POST[$fieldName] : null;\r\n}\r\n\r\n$errors = array();\r\n$errorCheck = false;\r\nforeach($properties[''validate''] as $fieldValidate){\r\n    if($fieldValidate == ''email'' && isset($fields[$fieldValidate]) && !empty($fields[$fieldValidate])) {\r\n        $errors[$fieldValidate] = validateEmail($fields[$fieldValidate]);\r\n        if($errors[$fieldValidate]) $errorCheck = true;\r\n    }\r\n    if(!$fields[$fieldValidate]) {\r\n        $errors[$fieldValidate] = true;\r\n        $errorCheck = true;\r\n    }\r\n}\r\n\r\n$output = array(''success''=>true);\r\n\r\nif($errorCheck) {\r\n    $output = array(\r\n        ''success''   =>  false,\r\n        ''errors''    =>  $errors\r\n    );\r\n} else {\r\n\r\n    if(!is_null($properties[''emailTo''])) {\r\n        $message = $modx->getChunk($properties[''tpl''], $fields);\r\n\r\n        $modx->getService(''mail'', ''mail.modPHPMailer'');\r\n        $modx->mail->set(modMail::MAIL_BODY,$message);\r\n        $modx->mail->set(modMail::MAIL_FROM,$modx->getOption(''emailsender''));\r\n        $modx->mail->set(modMail::MAIL_FROM_NAME,$modx->getOption(''site_name''));\r\n        $modx->mail->set(modMail::MAIL_SENDER,$modx->getOption(''site_name''));\r\n        $modx->mail->set(modMail::MAIL_SUBJECT,$properties[''subject'']);\r\n        $modx->mail->address(''to'',$properties[''emailTo'']);\r\n        $modx->mail->setHTML(true);\r\n        if (!$modx->mail->send()) {\r\n            $modx->log(modX::LOG_LEVEL_ERROR,''An error occurred while trying to send the email: ''.$err);\r\n        }\r\n        $modx->mail->reset();\r\n    }\r\n}\r\n\r\nreturn json_encode($output);', 0, 'a:5:{s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:18:"basetheme_prop_tpl";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:13:"emailContacts";s:7:"lexicon";s:20:"basetheme:properties";s:4:"area";s:0:"";}s:6:"fields";a:7:{s:4:"name";s:6:"fields";s:4:"desc";s:21:"basetheme_prop_fields";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:18:"name,email,message";s:7:"lexicon";s:20:"basetheme:properties";s:4:"area";s:0:"";}s:8:"validate";a:7:{s:4:"name";s:8:"validate";s:4:"desc";s:23:"basetheme_prop_validate";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:18:"name,email,message";s:7:"lexicon";s:20:"basetheme:properties";s:4:"area";s:0:"";}s:7:"emailTo";a:7:{s:4:"name";s:7:"emailTo";s:4:"desc";s:22:"basetheme_prop_emailTo";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:20:"basetheme:properties";s:4:"area";s:0:"";}s:7:"subject";a:7:{s:4:"name";s:7:"subject";s:4:"desc";s:22:"basetheme_prop_subject";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:17:"Message from site";s:7:"lexicon";s:20:"basetheme:properties";s:4:"area";s:0:"";}}', '', 1, 'assets/basetheme-elements/snippets/formContacts.php'),
(2, 1, 0, 'formSubscribe', '', 0, 1, 0, '/**\r\n * Description of uniApi\r\n *\r\n * @author ors\r\n */\r\nclass uniApi {\r\n\r\n    public $api_key;\r\n    public $lang = ''ru'';\r\n    private $langlist = array(''ru'');\r\n\r\n    /**\r\n     * Создаем объект с разрешенными языками\r\n     * @param string $key ключ доступа к API\r\n     * @param string $lang язык сообщений сервера API, в данный момент поддерживается ru, en, it\r\n     */\r\n    public function __construct($api_key, $lang = ''ru'') {\r\n        $this->api_key = $api_key;\r\n        if (in_array($lang, $this->langlist))\r\n            $this->lang = $lang; \r\n    } \r\n\r\n    /**\r\n     * Отправка запросов и обработка ошибок\r\n     * @param string $method название метода\r\n     * @param array $args аргументы метода, свои для каждого метода\r\n     */\r\n    private function uniRequest($method, $args) {\r\n        $post_array = array_merge(array(\r\n            ''api_key'' => $this->api_key,\r\n            ''lang'' => $this->lang,\r\n        ),$args);\r\n\r\n        $ch = curl_init();\r\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\r\n        curl_setopt($ch, CURLOPT_POST, 1);\r\n        curl_setopt($ch, CURLOPT_POSTFIELDS, $post_array);\r\n        curl_setopt($ch, CURLOPT_TIMEOUT, 10);\r\n        curl_setopt($ch, CURLOPT_URL,\r\n                ''http://api.unisender.com/ru/api/''.$method.''?format=json'');\r\n        $result = curl_exec($ch);\r\n\r\n        if ($result) {\r\n            // Раскодируем ответ API-сервера\r\n            $jsonObj = json_decode($result);\r\n\r\n            if (null === $jsonObj) {\r\n                // Ошибка в полученном ответе\r\n                echo "Invalid JSON";\r\n            } elseif (!empty($jsonObj->error)) {\r\n                // Ошибка добавления пользователя\r\n                echo "An error occured: " . $jsonObj->error . "(code: " . $jsonObj->code . ")";\r\n            }\r\n        } else {\r\n            // Ошибка соединения с API-сервером\r\n            echo "API access error";\r\n        }\r\n        return $jsonObj->result;\r\n    }\r\n    /**\r\n    * Этот метод добавляет контакты (e-mail адрес и/или мобильный телефон) подписчика в один или несколько списков, а также позволяет добавить/поменять значения дополнительных полей и меток.\r\n    * @param <type> $email\r\n    * @param <type> $list Перечисленные через запятую коды списков, в которые надо добавить подписчика.\r\n    * @param <type> $phone В случае наличия и e-mail, и телефона, подписчик будет включён и в e-mail, и в SMS списки рассылки.\r\n    * @param <type> $ip IP-адрес подписчика\r\n    * @param <type> $first_name\r\n    * @param <type> $last_name \r\n    */\r\n    public function subscribe($email, $list, $phone=NULL, $ip=NULL, $first_name=NULL, $last_name=NULL) {\r\n        $args = array (\r\n          ''list_ids'' => $list,\r\n          ''fields[email]'' => $email,\r\n          ''fields[Name]'' => $first_name.'' ''.$last_name,\r\n          ''request_ip'' => $ip,\r\n          ''overwrite'' => 2,\r\n          ''double_optin'' => 1\r\n        );\r\n        if ($phone)\r\n            $args[''fields[phone]'']=$phone;\r\n        $result=$this->uniRequest(''subscribe'', $args);\r\n        if ($result==''too_many_double_optins''){\r\n            $args[''double_optin'']=0;\r\n            $result=$this->uniRequest(''subscribe'', $args);\r\n        }\r\n//        return $result;\r\n    }\r\n}\r\n\r\nfunction validateEmail($email){\r\n    $pattern = "/^[^@]{1,64}\\@[^\\@]{1,255}$/";\r\n    $condition = @preg_match($pattern, $email);\r\n    if (!$condition) {\r\n        return true;\r\n    }\r\n\r\n    $email_array = explode("@", $email);\r\n    $local_array = explode(".", $email_array[0]);\r\n    for ($i = 0; $i < sizeof($local_array); $i++) {\r\n        $pattern = "/^(([A-Za-z0-9!#$%&''*+\\/=?^_`{|}~-][A-Za-z0-9!#$%&''*+\\/=?^_`{|}~\\.-]{0,63})|(\\"[^(\\\\|\\")]{0,62}\\"))$/";\r\n        $condition = @preg_match($pattern,$local_array[$i]);\r\n        if (!$condition) {\r\n            return true;\r\n        }\r\n    }\r\n\r\n    $pattern = "^\\[?[0-9\\.]+\\]?$";\r\n    $condition = @preg_match($pattern, $email_array[1]);\r\n    if (!$condition) {\r\n        $domain_array = explode(".", $email_array[1]);\r\n        if (sizeof($domain_array) < 2) {\r\n            return true;\r\n        }\r\n        for ($i = 0; $i < sizeof($domain_array); $i++) {\r\n            $pattern = "/^(([A-Za-z0-9][A-Za-z0-9-]{0,61}[A-Za-z0-9])|([A-Za-z0-9]+))$/";\r\n            $condition = @preg_match($pattern,$domain_array[$i]);\r\n            if (!$condition) {\r\n                return true;\r\n            }\r\n        }\r\n    }\r\n    return false;\r\n}\r\n\r\n$properties =& $scriptProperties;\r\n\r\n$properties[''email''] = isset($_POST[''email'']) ? $_POST[''email''] : null;\r\n\r\n$errors = array(\r\n    ''email'' => false\r\n);\r\n\r\n/* email */\r\n$errors[''email''] = validateEmail($properties[''email'']);\r\n/* end email */\r\n\r\n$output = array(''success''=>true);\r\n\r\n$errorCheck = false;\r\n$errorOutput = array();\r\nforeach ($errors as $name => $error) {\r\n    if($error) {\r\n        $errorCheck = true;\r\n        $errorOutput[$name] = true;\r\n    }\r\n}\r\n\r\nif($errorCheck) {\r\n    $output = array(\r\n        ''success''   =>  false,\r\n        ''errors''    =>  $errorOutput\r\n    );\r\n} else {\r\n    $uni = new uniApi($modx->getOption(''unisender_api_key''));\r\n\r\n    $uni->subscribe($properties[''email''], $modx->getOption(''unisender_list_ids''));\r\n}\r\n\r\nreturn json_encode($output);', 0, 'a:0:{}', '', 1, 'assets/basetheme-elements/snippets/formSubscribe.php'),
