  KEY `resource` (`resource`),
  KEY `comment_last` (`comment_last`),
  KEY `comments` (`comments`),
  KEY `closed` (`closed`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Структура таблицы `tickets_views`
--

CREATE TABLE IF NOT EXISTS `tickets_views` (
  `parent` int(10) unsigned NOT NULL DEFAULT '0',
  `uid` int(10) unsigned NOT NULL DEFAULT '0',
  `timestamp` datetime NOT NULL,
  PRIMARY KEY (`parent`,`uid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- --------------------------------------------------------

--
-- Структура таблицы `tickets_votes`
--

CREATE TABLE IF NOT EXISTS `tickets_votes` (
  `id` int(10) unsigned NOT NULL DEFAULT '0',
  `class` varchar(100) NOT NULL DEFAULT '',
  `owner` int(10) unsigned NOT NULL DEFAULT '0',
  `value` tinyint(1) NOT NULL DEFAULT '0',
  `createdon` datetime DEFAULT NULL,
  `createdby` int(10) unsigned NOT NULL DEFAULT '0',
  `ip` varchar(16) DEFAULT '0.0.0.0',
  PRIMARY KEY (`id`,`createdby`,`class`),
  KEY `createdon` (`createdon`),
  KEY `owner` (`owner`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

